import React, { useState, useEffect } from "react";
import {
  useAddEmployeesMutation,
  useGetOneEmployeesQuery,
  useUpdateEmployeesMutation,
} from "../../redux/services/DZapi";

function AddUpdateData({ id, setShow }) {
  // Initialize the state with an empty object or user data if available
  const [data, setData] = useState({
    name: "",
    number: "",
    address: "",
    roll: "",
    status: true,
    package: "",
    ...((userData.data && userData.data.attributes) || {}), // Set initial data if available
  });

  // Use separate mutation hooks for adding and updating user data
  const [addEmployee, { isLoading: isAdding, isError: addError, error: addErrorObj }] =
    useAddEmployeesMutation();

  const [updateEmployee, { isLoading: isUpdating, isError: updateError, error: updateErrorObj }] =
    useUpdateEmployeesMutation();

  const closePopup = () => {
    setShow(false);
  };

  // Fetch user data if an `id` is provided for updating
  const userData = useGetOneEmployeesQuery(id);

  useEffect(() => {
    if (id && !userData.isError && userData.data) {
      // Populate the form with the fetched user data
      setData(userData.data.attributes);
    }
  }, [id, userData]);

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleAddOrUpdateUser = async () => {
    try {
      // Determine whether to add or update based on the presence of `id`
      if (id) {
        // Update user data
        const response = await updateEmployee({ id, data });
        // Handle successful user update here
        console.log("User updated successfully:", response);
      } else {
        // Add a new user
        const response = await addEmployee({ data });
        // Handle successful user creation here
        console.log("User added successfully:", response);
      }
      closePopup();
    } catch (err) {
      // Handle error (e.g., show an error message)
      console.error("Error adding/updating user:", err);
    }
  };

  return (
    <div className="address__popup" id="address__popup">
      <h1 className="subheading">Add Address</h1>

      <div className="address__fields">
      <div className="address__section">
          <div className="input">
            <label htmlFor="name" required="">
              Name<span>*</span>
            </label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Name"
              value={data?.name}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="number" required="">
              Number<span>*</span>
            </label>
            <input
              type="text"
              id="number"
              name="number"
              placeholder="Number"
              value={data?.number}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="address" required="">
              Address<span>*</span>
            </label>
            <input
              type="text"
              id="address"
              name="address"
              placeholder="Address"
              value={data?.address}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="roll" required="">
              Roll<span>*</span>
            </label>
            <input
              type="text"
              id="roll"
              name="roll"
              placeholder="Roll"
              value={data?.roll}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="package" required="">
              Package<span>*</span>
            </label>
            <input
              type="text"
              id="package"
              name="package"
              placeholder="Package"
              value={data?.package}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="status" required="">
              Status<span>*</span>
            </label>
            <select
              id="status"
              name="status"
              value={data?.status}
              onChange={handleChange}
            >
              <option value={true}>Active</option>
              <option value={false}>Inactive</option>
            </select>
          </div>
        </div>
      </div>

      <div className="btns">
        <button className="btn" onClick={handleAddOrUpdateUser}>
          {isAdding || isUpdating ? "Adding/Updating..." : "Submit"}
        </button>
        <button className="btn" onClick={closePopup}>
          Cancel
        </button>
      </div>

      {(addError || updateError) && <div className="error-message">Error: {addError ? addErrorObj.message : updateErrorObj.message}</div>}
    </div>
  );
}

export default AddUpdateData;
