import React, { useEffect, useState } from "react";
import {
  useAddEmployeesMutation,
  useGetOneEmployeesQuery,
  useUpdateEmployeesMutation,
} from "../../redux/services/DZapi";

function AddUpdateData({ id, setShow }) {
console.log(id)

  const [updateEmployee, { isLoading, isError, error }] = useUpdateEmployeesMutation();
  const [addEmployee, { AddisLoading, AddisError, Adderror }] = useAddEmployeesMutation();
  const [getOneEmployees, { GetoneisLoading, GetoneisError, Getoneerror }] =useGetOneEmployeesQuery();
  const [oneuserdata, setOneuserdata] = useState()  

  
  const [data, setData] = useState({
    name: "",
    number: "",
    address: "",
    roll: "",
    status: true,
    package: "",
  });

useEffect(()=>{
  if(id){

    setData({
      name:getOneEmployees.data.name,
      number:getOneEmployees.data.number,
      address:getOneEmployees.data.address,
      roll:getOneEmployees.data.roll,
      status:getOneEmployees.data.status,
      package:getOneEmployees.data.package,
    })
  }
})
  const closePopup = () => {
    setShow(false);
  };

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleAddUser = async () => {
    try {
      const response = await addEmployee({ data });
      // Handle successful user creation here (e.g., show a success message)
      console.log("User added successfully:", response);
      closePopup();
    } catch (err) {
      // Handle error (e.g., show an error message)
      console.error("Error adding user:", err);
    }
  };

  const handleUpdateUser = async () => {
    try {
      console.log(id)
      const response = await updateEmployee({  data,id });
      // Handle successful user creation here (e

      console.log("User updated successfully:", response);
      closePopup();
    } catch (err) {
      // Handle error (e.g., show an error message)
      console.error("Error updating user:", err);
    }
  };

  const handleaddupdateuser = ()=> {
    if (id) {
      handleUpdateUser();
      console.log("handleUpdateUser");
    } else {
      handleAddUser();
      console.log("handleAddUser");
    }
  }
  return (
    <div className="address__popup" id="address__popup">
      <h1 className="subheading">Add Address</h1>

      <div className="address__fields">
        <div className="address__section">
          <div className="input">
            <label htmlFor="name" required="">
              Name<span>*</span>
            </label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Name"
              value={data.name}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="number" required="">
              Number<span>*</span>
            </label>
            <input
              type="text"
              id="number"
              name="number"
              placeholder="Number"
              value={data.number}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="address" required="">
              Address<span>*</span>
            </label>
            <input
              type="text"
              id="address"
              name="address"
              placeholder="Address"
              value={data.address}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="roll" required="">
              Roll<span>*</span>
            </label>
            <input
              type="text"
              id="roll"
              name="roll"
              placeholder="Roll"
              value={data.roll}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="package" required="">
              Package<span>*</span>
            </label>
            <input
              type="text"
              id="package"
              name="package"
              placeholder="Package"
              value={data.package}
              onChange={handleChange}
            />
          </div>
          <div className="input">
            <label htmlFor="status" required="">
              Status<span>*</span>
            </label>
            <select
              id="status"
              name="status"
              value={data.status}
              onChange={handleChange}
            >
              <option value={true}>Active</option>
              <option value={false}>Inactive</option>
            </select>
          </div>
        </div>
      </div>

      <div className="btns">
        <button className="btn" onClick={handleaddupdateuser}>
          {isLoading ? "Adding..." : "Submit"}
        </button>
        <button className="btn" onClick={closePopup}>
          Cancel
        </button>
      </div>

      {isError && <div className="error-message">Error: {error.message}</div>}
    </div>
  );
}

export default AddUpdateData;
