import React, { useState, useEffect, useRef } from "react";
import {
  useDeleteEmployeesMutation,
  useGetAllPackagesQuery,
  useGetAllEmployeesRoleQuery,
} from "../../redux/services/DZapi";

function Table({ getallemployes, openPopup, setShow, refetch }) {
  const { data: allPackages } = useGetAllPackagesQuery();
  const { data: rolesData } = useGetAllEmployeesRoleQuery();
  const [showRoleDrop, setShowRoleDrop] = useState(false);
  const [showPackageDrop, setShowPackageDrop] = useState(false);
  const [showActiveFilter, setShowActiveFilter] = useState(false);
  const [showInactiveFilter, setShowInactiveFilter] = useState(false);
  const roleDropRef = useRef(null);
  const packageDropRef = useRef(null);
  const activeFilterRef = useRef(null);
  const inactiveFilterRef = useRef(null);
  const [nameFilter, setNameFilter] = useState("");
  const [selectedRoleFilter, setSelectedRoleFilter] = useState("User");
  const [selectedPackageFilter, setSelectedPackageFilter] = useState("All");
  const [selectedStatusFilter, setSelectedStatusFilter] = useState("All");
  const [selectedAttributeFilter, setSelectedAttributeFilter] = useState("name"); // New state for attribute filter
  const [deleteEmployees] = useDeleteEmployeesMutation();

  const handleDeleteEmployee = async (employeeId) => {
    try {
      await deleteEmployees(employeeId);
      refetch();
    } catch (error) {}
  };

  const filteredUsers = getallemployes?.filter((adata) => {
    const data = adata?.attributes;
    return (
      (data?.name.toLowerCase().includes(nameFilter.toLowerCase()) &&
        (selectedRoleFilter === "All" || data?.roll === selectedRoleFilter) &&
        (selectedPackageFilter === "All" || data?.package === selectedPackageFilter) &&
        (selectedStatusFilter === "All" || data?.status === (selectedStatusFilter === "Active")) &&
        (selectedAttributeFilter === "name" ? data?.name.toLowerCase().includes(nameFilter.toLowerCase()) : true) &&
        (selectedAttributeFilter === "cnic" ? data?.cnic.toLowerCase().includes(nameFilter.toLowerCase()) : true) &&
        (selectedAttributeFilter === "phone" ? data?.phone.toLowerCase().includes(nameFilter.toLowerCase()) : true) &&
        (selectedAttributeFilter === "package" ? data?.package.toLowerCase().includes(nameFilter.toLowerCase()) : true) &&
        (selectedAttributeFilter === "address" ? data?.address.toLowerCase().includes(nameFilter.toLowerCase()) : true) &&
        (selectedAttributeFilter === "roll" ? data?.roll.toLowerCase().includes(nameFilter.toLowerCase()) : true))
    );
  });

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (roleDropRef.current && !roleDropRef.current.contains(event.target)) {
        setShowRoleDrop(false);
      }
      if (packageDropRef.current && !packageDropRef.current.contains(event.target)) {
        setShowPackageDrop(false);
      }
      if (activeFilterRef.current && !activeFilterRef.current.contains(event.target)) {
        setShowActiveFilter(false);
      }
      if (inactiveFilterRef.current && !inactiveFilterRef.current.contains(event.target)) {
        setShowInactiveFilter(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  });

  const toggleRoleDrop = () => {
    setShowRoleDrop(!showRoleDrop);
  };

  const togglePackageDrop = () => {
    setShowPackageDrop(!showPackageDrop);
  };

  const toggleActiveFilter = () => {
    setShowActiveFilter(!showActiveFilter);
  };

  const toggleInactiveFilter = () => {
    setShowInactiveFilter(!showInactiveFilter);
  };

  const handleRoleSelect = (role) => {
    setSelectedRoleFilter(role);
    setShowRoleDrop(false);
  };

  const handlePackageSelect = (pkg) => {
    setSelectedPackageFilter(pkg);
    setShowPackageDrop(false);
  };

  const handleStatusSelect = (status) => {
    setSelectedStatusFilter(status);
    setShowActiveFilter(false);
    setShowInactiveFilter(false);
  };

  const handleAttributeSelect = (attribute) => {
    setSelectedAttributeFilter(attribute); // Update the selected attribute filter
  };
  return (
    <>
      <div className="category__filter">
        <div className="categories">

          <div
            className={`user__icon category ${
              selectedRoleFilter !== "All" ? "active__categary" : null
            }`}
          >
            {selectedRoleFilter !== "All" ? (
              <i
                class="ri-close-line"
                onClick={() => setSelectedRoleFilter("All")} // Clear the role filter
              ></i>
            ) : null}
            <div>
            <span onClick={toggleRoleDrop}>
              {selectedRoleFilter !== "All" ? `${selectedRoleFilter}` : "Role"}
            </span>
            <i class="ri-arrow-down-s-line"></i>
            </div>
            {showRoleDrop && (
              <div className="user__drop" ref={roleDropRef}>
                {rolesData?.data?.map((role) => (
                  <span
                    key={role.id}
                    className="span"
                    onClick={() => handleRoleSelect(role.attributes.rollTitle)} // Handle role selection
                  >
                    {role.attributes.rollTitle}
                  </span>
                ))}
              </div>
            )}
          </div>
          {/* package filter ? */}
          <div className={`user__icon category ${ 
              selectedPackageFilter !== "All" ? "active__categary" : null
            }`}>
            {selectedPackageFilter !== "All"?<i
              class="ri-close-line"
              onClick={() => setSelectedPackageFilter("All")}
            ></i>: null}
            <span onClick={togglePackageDrop}>
              Package {selectedPackageFilter !== "All" && `(${selectedPackageFilter})`}
            </span>
            <i class="ri-arrow-down-s-line"></i>
            {showPackageDrop && (
              <div className="user__drop" ref={packageDropRef}>
                {allPackages?.data?.map((pkg) => (
                  <span
                    key={pkg.id}
                    className="span"
                    onClick={() => handlePackageSelect(pkg.attributes.name)} // Handle package selection
                  >
                    {pkg.attributes.name}
                  </span>
                ))}
              </div>
            )}
          </div>
          

          {/* activer filter added */}
          <div
            className={`user__icon category ${
              selectedStatusFilter !== "All" ? "active__categary" : null
            }`}
          >
            {selectedStatusFilter !== "All" ? (
              <i
                class="ri-close-line"
                onClick={() => setSelectedStatusFilter("All")}
              ></i>
            ) : null}
            <div>
              <span onClick={toggleActiveFilter}>
                {selectedStatusFilter !== "All" ? `${selectedStatusFilter} Users` : "Status"}
              </span>
              <i class="ri-arrow-down-s-line"></i>
            </div>
            {showActiveFilter && (
              <div className="user__drop" ref={activeFilterRef}>
                <span
                  key="active"
                  className="span"
                  onClick={() => handleStatusSelect("Active")}
                >
                  Active
                </span>
                <span
                  key="inactive"
                  className="span"
                  onClick={() => handleStatusSelect("Inactive")}
                >
                  Inactive
                </span>
              </div>
            )}
          </div>




          {/* <div className="category ">
            <span>select option for search</span>
            <i class="ri-arrow-down-s-line"></i>
          </div> */}
 <div
            className={`user__icon category ${
              selectedAttributeFilter !== "name" ? "active__categary" : null
            }`}
          >
            {selectedAttributeFilter !== "name" ? (
              <i
                class="ri-close-line"
                onClick={() => handleAttributeSelect("name")} // Clear the attribute filter
              ></i>
            ) : null}
            <div>
              <span onClick={() => handleAttributeSelect("name")}>
                {selectedAttributeFilter !== "name" ? "Name" : "Attribute"}
              </span>
              <i class="ri-arrow-down-s-line"></i>
            </div>
          </div>
        </div>
        <div className="filter__input">
          <input
            type="text"
            placeholder={`Filter by ${selectedAttributeFilter.charAt(0).toUpperCase() + selectedAttributeFilter.slice(1)}`} // Display the selected attribute in the placeholder
            value={nameFilter}
            onChange={(e) => setNameFilter(e.target.value)}
          />
        </div>
      </div>
      <div className="table">
        <div className="filter__row">
          <div className="filter__input">
            <span>All Data</span>
          </div>
          <button className="btn" onClick={() => openPopup()}>
            <i className="ri-add-circle-line"></i>
            Add New Client
          </button>
        </div>
        <div className="rows__data">
          <div className="row heading__row">
            <div className="data check">
              <input type="checkbox" />
            </div>
            <div className="data check">
              <span className="data__heading">#ID</span>
            </div>
            <div className="data">
              <span className="data__heading client__name">Name</span>
            </div>
            <div className="data">
              <span className="data__heading client__name">Customer Id</span>
            </div>
            <div className="data">
              <span className="data__heading client__name">Password</span>
            </div>
            <div className="data">
              <span className="data__heading">Phone</span>
            </div>
            <div className="data">
              <span className="data__heading">CNIC</span>
            </div>
            <div className="data">
              <span className="data__heading">Package</span>
            </div>
            <div className="data address__data">
              <span className="data__heading">Address</span>
            </div>
            <div className="data">
              <span className="data__heading">Role</span>
            </div>
            <div className="data">
              <span className="data__heading">Status</span>
            </div>
            <div className="data">
              <span className="data__heading">Comment</span>
            </div>
            <div className="data">
              <span className="data__heading">Action</span>
            </div>
          </div>

          {filteredUsers?.map((adata, index) => {
            const data = adata?.attributes;
            return (
              <div className="row" key={index}>
                <div className="data check">
                  <input type="checkbox" />
                </div>
                <div className="data check">
                  <span className="data__content user__id">{index + 1}</span>
                </div>
                <div className="data ">
                  <span className="data__content client__name">
                    {data?.name}
                  </span>
                </div>
                <div className="data ">
                  <span className="data__content client__name">
                    {data?.CustomerId}
                  </span>
                </div>
                <div className="data ">
                  <span className="data__content client__name">
                    {data?.password}
                  </span>
                </div>
                <div className="data">
                  <span className="data__content">{data?.number}</span>
                </div>
                <div className="data">
                  <span className="data__content">{data?.cnic}</span>
                </div>
                <div className="data">
                  <span className="data__content">{data?.package}</span>
                </div>
                <div className="data address__data">
                  <span className="data__content" title={data?.address}>
                    {data?.address}
                  </span>
                </div>
                <div className="data">
                  <span className="data__content paid">{data?.roll}</span>
                </div>

                <div className="data">
                  <span
                    className="data__content paid"
                    style={{
                      backgroundColor: data?.status ? "#72E1281F" : "red",
                    }}
                  >
                    {data?.status ? "Active" : "Inactive"}
                  </span>
                </div>
                <div className="data">
                  <span className="data__content paid">{data?.comment}</span>
                </div>
                <div className="data">
                  <span className="data__content action">
                    <i
                      className="ri-eye-line"
                      onClick={() => openPopup(adata?.id, true)}
                    ></i>
                    <i
                      className="ri-edit-2-line"
                      onClick={() => openPopup(adata?.id)}
                    ></i>
                    <i
                      className="ri-delete-bin-2-line"
                      onClick={() => handleDeleteEmployee(adata?.id)}
                    ></i>
                  </span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}
export default Table;
